    private void cmdEncodeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdEncodeActionPerformed
        String name = txtInput.getText();
        name = name +" ";
        String a = "";
        int p = name.length();
        char ch;
        int x, y;
        char b = ' ';
        String c, d;
        for (int m = 0; m < p; m++) {
            ch = name.charAt(m);
            if (ch == ' ') {
                x = a.length();
                for (y = 0; y < x; y++) {
                    b = a.charAt(y);
                    if (b == 'a' || b == 'e' || b == 'i' || b == 'o' || b == 'u' || b == 'A' || b == 'E' || b == 'I' || b == 'O' || b == 'U') {
                        break;
                    }
                }
                c = a.substring(y, x);
                d = a.substring(0, y);
                if (Character.isUpperCase(b)) {
                    txtOutput.append(c + d + "AY" + ' ');
                } else {
                    txtOutput.append(c + d + "ay" + ' ');
                }
                a = "";
            } else {
                a = a + ch;
            }
        }
    }//GEN-LAST:event_cmdEncodeActionPerformed

